import React from "react";

class Book extends React.Component {
  state = {
    comments: [],
    newComment: "",
    commentsForm: false
  }

  handleAddCommentClick = () => {
    this.setState({
      commentsForm: !this.state.commentsForm
    })
  }


  render(){
    // console.log("Book props", this.props)
    return (
      <div>
        <h2>{this.props.book.title}</h2>
        <img src = {this.props.book.img} />
        <br></br>
        <button onClick = {this.handleAddCommentClick}>Add comment</button>
          {this.state.commentsForm ? <CommentsForm />: null}
        <br></br>
        <button>All comments</button>
      </div>
    );
  }
};

export default Book;
