import React, { Component } from "react";
import Form from "../components/Form";
import Book from "../components/Book"
// import SearchForm from "../components/SearchForm"

class BookList extends Component {

  state = {
    books: []
  }

  componentDidMount(){
    fetch("http://localhost:3005/books")
      .then(res => res.json())
      .then(data => this.setState({
        books: data
      }))
  }

  addBook = (book) => {
    this.setState({
      books: [book, ...this.state.books]
    })
  }

  // handleSearch = (e) => {
  //   this.setState({
  //     searchTerm: e.target.value
  //   })
  // }

  // filterBooks = () => {
  //   console.log("serch term", this.state.searchTerm)
  //   console.log("In filter", this.state.books.filter(book => book.title.includes(this.state.searchTerm)))
  //   // return this.state.books.filter(book => book.title.toUpperCase().includes(this.state.searchTerm.toUpperCase()))
  // }

  render(){
    // console.log(this.state.books)
    let books = this.state.books.map(bookObj => <Book key = {bookObj.title} book = {bookObj}/>)
    // let books = this.filterBooks().map(bookObj => <Book key = {bookObj.title} book = {bookObj}/>)
    return (
      <div className="book-list">
        <h1>Book List</h1>

        <Form handleSubmit = {this.addBook}/>

        {/* <SearchForm handleSearch = {this.handleSearch} searchTerm = {this.state.searchTerm}/> */}

        <ul>{books}</ul>
      </div>
    );
  }
}

export default BookList;
