import React from "react";
import Book from "../components/Book";
import SearchForm from "../components/SearchForm"

class Bookshelf extends React.Component {
  state = {
    search: ""
  }

  handleSearch = (e) => {
    this.setState({
      search: e.target.value
    })
  }

  filterBookList = () => {
    return this.props.book.filter(book => book.title.includes(this.state.searchTerm))
  }


  render(){
    // console.log("bookshelf props", this.props.book)
    //can currently add duplicates
    // let books = this.props.book.map(bookObj => <Book key = {bookObj.id} book = {bookObj} handleClick = {this.props.handleClick}/>)
    // console.log(books)
    let books = this.filterBookList().map(bookObj => <Book key = {bookObj.id} book = {bookObj} handleClick = {this.props.handleClick}/>)

    return (
      <div>
        <h1>Book Shelf</h1>
        <SearchForm handleSearch = {this.handleSearch}/>
        <ul>{books}</ul>
      </div>
    );
  }
};


export default Bookshelf;
